package just.test;

public class Increment {

	public static void main(String[] args) {
		
		
		int a = 5;
		
		System.out.println(a );
		
		++a;	
		
		System.out.println("/////////" );
		
		System.out.println(a );
		
				
		System.out.println("////////" );
		
		System.out.println(a++ );
		
		System.out.println("////////" );
		
		System.out.println(a );
		
		// TODO Auto-generated method stub

	}

}
